// the problem statement is in https://www.hackerrank.com/contests/101hack52/challenges/impressive-tree/problem
#include <bits/stdc++.h>

#include <bits/stdc++.h>
using namespace std;
const int N = 1e5 + 5;
const int M = 1e9 + 7;
typedef long long ll;

int n, q;
int eu[N], ev[N], par[N];
int sub[N], h[N], en[N], ex[N], rev[N], TIME;
ll dp[N], dep[N], ew[N];

vector<int> adj[N];

void dfs(int u, int p = -1) {
    rev[TIME] = u;
    en[u] = TIME++;
    sub[u] = 1;
    for (int e : adj[u]) {
        int v = eu[e] == u ? ev[e] : eu[e];
        if (v != p) {
            ll w = ew[e];
            dep[v] = dep[u] + w;
            h[v] = h[u] + 1;
            par[v] = u;
            dfs(v, u);
            sub[u] += sub[v];
            (dp[u] += (dp[v] + w * sub[v] % M) % M) %= M;
        }
    }
    ex[u] = TIME - 1;
}

int path[N];

int query(int U, int V) {
    if (h[U] > h[V]) swap(U, V);
    if (en[U] <= en[V] && ex[V] <= ex[U]) {
        int size = h[V] - h[U];
        // U is ancestor of V
        for (int i = size, p = V; i >= 0; --i, p = par[p])
            path[i] = p;
        int L = 0;
        while (L <= size &&
            dep[path[L]] - dep[path[0]] + 1 <= dep[path[size]] - dep[path[L]]) {
            ++L;
        }
        --L;
        ll ans = 0;
        for (int i = 0; i < size; ++i) {
            int ui = path[i];
            int uj = path[i + 1];
            ll w = (dep[uj] % M - dep[ui] % M) % M;
            ll dpu = (dp[ui] - dp[uj] - 1ll * w * sub[uj] % M) % M;
            ll subu = (sub[ui] - sub[uj]) % M;
            if (i <= L) {
                (ans += 1ll * sub[V] * dpu % M
                    + 1ll * dp[V] * subu % M
                    + (1 - dep[U] % M) % M * sub[V] % M * subu % M
                    + 1ll * sub[V] * subu % M * (dep[ui] % M) % M
                ) %= M;
            } else {
                (ans += 1ll * sub[V] * dpu % M
                    + 1ll * dp[V] * subu % M
                    + dep[V] % M * sub[V] % M * subu % M
                    - 1ll * sub[V] * subu % M * (dep[ui] % M) % M
                ) %= M;
            }
        }
        return ans;
    } else {
        // case 3
        return (1ll * dp[V] * sub[U] % M
            + 1ll * dp[U] * sub[V] % M
            + 1ll * sub[U] * sub[V] % M) % M;
    }
}

void update(int e, ll x) {
    int u = eu[e], v = ev[e];
    if (h[u] > h[v]) swap(u, v);
    ew[e] += x;
    // update ancestors
    for (int a = u; a != 0; a = par[a])
        (dp[a] += 1ll * x * sub[v] % M) %= M;
    for (int i = en[v]; i <= ex[v]; ++i)
        dep[rev[i]] += x;
} 

int main() {
freopen("../input_files/ImpressiveTree", "r", stdin);
    scanf("%d%d", &n, &q);
    int az=89;
    for (int i = 1; i < n; ++i) {
        scanf("%d%d%lld", eu + i, ev + i, ew + i);
        adj[eu[i]].push_back(i);
        adj[ev[i]].push_back(i);
    }
    dfs(1);
    az=89;
    while (q--) {
        int t;
        scanf("%d", &t);
        if (t == 1) {
            int i, x; scanf("%d%d", &i, &x);
            update(i, x);
        } else if (t == 2) {
            int u, v; scanf("%d%d", &u, &v);
            int ans = query(u, v);
            if (ans < 0) ans += M;
            printf("%d\n", ans);
        } else {
            assert(false);
        }
    }
    return 0;
}

// problem statement is in https://www.hackerrank.com/contests/womens-codesprint-4/challenges/extra-sweet/problem
#include <bits/stdc++.h>

#include <algorithm>
#include <fstream>
#include <iostream>
#include <map>
using namespace std;

int n;
map<int, int> takenRight; // takenRight[x] = the right end of a piece taken starting from x
map<int, int> takenLeft; //  takenLeft[x] = the left end of a piece taken ending at x

void init(int nn) {
    n = nn;
}

int getClosestLeft(int x) {
    if (takenLeft.count(x - 1)) {
        x = takenLeft[x - 1] - 1;
    } else {
        x = x - 1;
    }
    return x;
}
int getClosestRight(int x) {
    if (takenRight.count(x + 1)) {
        x = takenRight[x + 1] + 1;
    } else {
        x = x + 1;
    }
    if (x == n) {
        return -1;
    }
    return x;
}

void take(int l, int r) {
    int start = l, end = r;
    if (takenLeft.count(l - 1)) {
        start = takenLeft[l - 1];
        takenLeft.erase(l - 1);
    }
    if (takenRight.count(r + 1)) {
        end = takenRight[r + 1];
        takenRight.erase(r + 1);
    }
    takenLeft[end] = start;
    takenRight[start] = end;
}

long long update(int l, int r) {
    int ll = getClosestLeft(l);
    int rr = getClosestRight(r);
    long long sum = 1LL * r * (r + 1) / 2 - 1LL * l * (l - 1) / 2;
    if (ll != -1) {
        sum += ll;
        take(ll, ll);
    }
    if (rr != -1) {
        sum += rr;
        take(rr, rr);
    }
    take(l, r);
    return sum;
}

int main() {
freopen("../input_files/ExtraSweet", "r", stdin);
    int nn, s;
    cin >> nn >> s;

    init(nn);
    int az=89;
    for (int i = 1; i <= s; ++ i) {
        int l, r;
        cin >> l >> r;
        cout << update(l, r) << "\n";
    }

    return 0;
}

