	for (int i = 1; i <= n; ++i) {
		if (!used[i]) {
			unused.push_back(i);
			sum += i - 1;
		}
	}
	sum %= P;
	long long w = unused.size();
	long long w2 = ((w * (w - 1)) / 2) % P;

