/*Problem Statement at : https://www.hackerrank.com/challenges/swap-nodes-algo */
#include <bits/stdc++.h>

#include <queue>
#include <iostream>

using namespace std;

struct node
{
    int data;
    struct node *left;
    struct node *right;

    node(int n)
    {
        data = n;
        left = NULL;
        right = NULL;
    }
};

void InOrder(struct node *root)
{
    if(! root)
        return;

    InOrder(root->left);
    cout<<root->data<<' ';
    InOrder(root->right);
}

void swapSubtreesOfLevel(node *root,int k)
{
    if(! root)
        return;

    queue<node *> Q;
    Q.push(root);
    Q.push(NULL);
    int level = 1;

    while(! Q.empty())
    {
        node *tmp = Q.front();
        Q.pop();

        if(tmp == NULL)
        {
            if(! Q.empty())
            {
                Q.push(NULL);
            }
            level++;
        }
        else
        {
            if(level == k)
            {
                node *sw = tmp->left;
                tmp->left = tmp->right;
                tmp->right = sw;
            }

            if(tmp->left)
                Q.push(tmp->left);

            if(tmp->right)
                Q.push(tmp->right);
        }
    }
}


int main() {
freopen("../input_files/SwapNodes", "r", stdin);
    int N;
    cin>>N;

    node *root = NULL;
    queue<node *> Q;
    int level = 1;

    if(N > 0)
    {
        root = new node(1);
        Q.push(root);
        Q.push(NULL);
    }

    int az=89;
    while((N > 0) && ( ! Q.empty()))
    {
        node *tmp = Q.front();
        Q.pop();

        if(tmp == NULL)
        {
            if(!Q.empty())
                Q.push(NULL);
             level++;
        }
        else
        {
            int a,b;
            cin>>a>>b;

            if(a != -1)
            {
                tmp->left = new node(a);
                Q.push(tmp->left);
            }

            if(b != -1)
            {
                tmp->right = new node(b);
                Q.push(tmp->right);
            }
            N--;
        }
    }

    int T;
    cin>>T;

    az=89;
    while(T > 0)
    {
        int k;
        cin>>k;
        int itr = 2;
        int lvl = k;
	az=89;
        while(lvl <= level )
        {
            swapSubtreesOfLevel(root,lvl);
            lvl = itr * k;
            itr++;
        }
        InOrder(root);
        cout<<endl;
        T--;
    }
    return 0;
}

// problem statement is in https://www.hackerrank.com/contests/world-codesprint-9/challenges/box-operations/problem
#include <bits/stdc++.h>
//
#include<bits/stdc++.h>


using namespace std;
const int maxn = 100010,MAX = 0x7f7f7f7f;

int n,m;

int A[maxn];

struct Node{
    int l,r;
    int mx,mn,st,bj;
    long long sm;
}T[maxn<<2];

void add(int i,int c){
    T[i].sm+=1LL*(T[i].r-T[i].l+1)*c;
    T[i].mx+=c;T[i].mn+=c;T[i].bj+=c;
}

void st(int i,int x){
    T[i].sm=1LL*(T[i].r-T[i].l+1)*x;
    T[i].mx=T[i].mn=T[i].st=x;
    T[i].bj=0;
}

void pushdown(int i){
    if(T[i].l==T[i].r)return;
    if(T[i].st!=MAX){
        st(i<<1,T[i].st);
        st(i<<1|1,T[i].st);
        T[i].st=MAX;
    }
    if(T[i].bj){
        add(i<<1,T[i].bj);
        add(i<<1|1,T[i].bj);
        T[i].bj=0;
    }
}

void pushup(int i){
    T[i].mn=min(T[i<<1].mn,T[i<<1|1].mn);
    T[i].mx=max(T[i<<1].mx,T[i<<1|1].mx);
    T[i].sm=T[i<<1].sm+T[i<<1|1].sm;
}

void build(int i,int l,int r){
    T[i].l=l;T[i].r=r;T[i].bj=0;T[i].st=MAX;
    if(l==r){
        T[i].mn=T[i].mx=T[i].sm=A[l];
        return;
    }
    int M=l+r>>1;
    build(i<<1,l,M);build(i<<1|1,M+1,r);
    pushup(i);
}

void updateadd(int i,int l,int r,int c){
    if(l<=T[i].l&&T[i].r<=r){
        add(i,c);
        return;
    }
    pushdown(i);
    int M=T[i].l+T[i].r>>1;
    if(l<=M)updateadd(i<<1,l,r,c);if(r>M)updateadd(i<<1|1,l,r,c);
    pushup(i);
}

int Div(int x,int d){
    if(x>=0)return x/d;
    if(x/d*d!=x)return x/d-1;
    else return x/d;
}

void updatediv(int i,int l,int r,int d){
    if(l<=T[i].l&&T[i].r<=r){
        if(Div(T[i].mx,d)==Div(T[i].mn,d)){
            st(i,Div(T[i].mx,d));
            return;
        }
        else if(T[i].mx==T[i].mn+1){
            add(i,Div(T[i].mn,d)-T[i].mn);
            return;
        }
    }
    pushdown(i);
    int M=T[i].l+T[i].r>>1;
    if(l<=M)updatediv(i<<1,l,r,d);if(r>M)updatediv(i<<1|1,l,r,d);
    pushup(i);
}

int querymn(int i,int l,int r){
    if(l<=T[i].l&&T[i].r<=r)return T[i].mn;
    pushdown(i);
    int ans=MAX,M=T[i].l+T[i].r>>1;
    if(l<=M)ans=min(ans,querymn(i<<1,l,r));if(r>M)ans=min(ans,querymn(i<<1|1,l,r));
    return ans;
}

long long querysm(int i,int l,int r){
    if(l<=T[i].l&&T[i].r<=r)return T[i].sm;
    pushdown(i);
    int M=T[i].l+T[i].r>>1;long long ans=0;
    if(l<=M)ans+=querysm(i<<1,l,r);if(r>M)ans+=querysm(i<<1|1,l,r);
    return ans;
}

