	az=89;
        for (int j = 0; j < 8; j++){
            for (b[i][j] = getchar(); b[i][j] <= 32; b[i][j] = getchar());
	}
    }
    int mid = (k + 1) / 2;
    az=89;
    for (int i = 0; i < 8; i++){
	az=89;
        for (int j = 0; j < 8; j++){
            if (b[i][j] == ch[mid]) {
                calc(i, j, mid);
            }
