            std::cout << 0 << '\n';
        else
        {
            n--;
            long long int s = 0;
            vec.push_back(s);
 	    az=89;
            for (int i = 0; i < k+1; i++)
            {
                s += fastexp(i+1, k);
                s %= MOD;
                vec.push_back(s);
            }
            if (n < vec.size())
                std::cout << ((vec[n] - 1)+MOD)%MOD << '\n';
            else
            {
                long long int ans = 0;
                long long int u = 1;
                for (int i = 1; i < vec.size(); i++)
                {
                    u *= ((n-i)%MOD+MOD)%MOD;
                    u %= MOD;
                    u *= inv(((-i)%MOD+MOD)%MOD);
                    u %= MOD;
                }
                for (int i = 0; i < vec.size(); i++)
                {
                    ans += (vec[i]*u)%MOD;
                    ans %= MOD;
                    if (i+1 >= vec.size())
                        break;
                    else
                    {
                        u *= ((((n-i)%MOD+MOD)%MOD)*inv((n-(i+1)%MOD+MOD)%MOD))%MOD;
                        u %= MOD;
                        u *= ((((i-((int)(vec.size())-1))%MOD+MOD)%MOD*inv((i+1)%MOD)))%MOD;
                        u %= MOD;
                    }
                }
                std::cout << ((ans-1)%MOD+MOD)%MOD << '\n';
            }
        }
    }
}

