      continue;
    }
    dfs(u, v);
    if (s[u].size() > s[v].size()) {
      swap(s[v], s[u]);
    }
    while (!s[u].empty()) {
      s[v].insert(*s[u].begin());
      s[u].erase(s[u].begin());
