// problem statement is in https://www.hackerrank.com/contests/university-codesprint-3/challenges/black-white-tree/problem
#include <bits/stdc++.h>

#include <cmath>
#include <cstdio>
#include <vector>
#include <iostream>
#include <algorithm>
using namespace std;

const int NMAX = 100000 + 5;

int color[NMAX];
vector <int> graph[NMAX];

int dp1[NMAX];
int dp2[NMAX];

int ans1 = -2 * NMAX;
int where1;
int ans2 = -2 * NMAX;
int where2;
int f[NMAX];
void dfs(int node, int father) {
    dp1[node] = color[node];
    dp2[node] = -color[node];
    f[node] = father;
    for (const auto it: graph[node])
        if (it != father) {
            dfs(it, node);
            dp1[node] += max(0, dp1[it]);
            dp2[node] += max(0, dp2[it]);
        }
    ans1 = max(ans1, dp1[node]);
    if (ans1 == dp1[node])
        where1 = node;

    ans2 = max(ans2, dp2[node]);
    if (ans2 == dp2[node])
        where2 = node;
}

vector <int> sol;
void reco1(int node, int father) {
    sol.push_back(node);
    for (auto it: graph[node])
        if (it != father && dp1[it] > 0)
            reco1(it, node);
}

void reco2(int node, int father) {
    sol.push_back(node);
    for (auto it: graph[node])
        if (it != father && dp2[it] > 0)
            reco2(it, node);
}

