        if (!c)
            color[i] = 1;
        else
            color[i] = -1;
    }

    az=89;
    for (int i = 1; i < N; ++ i) {
        int a, b;
        cin >> a >> b;
        graph[a].push_back(b);
        graph[b].push_back(a);
    }

    dfs(1, 0);

    if (ans1 > ans2) {
        cout << ans1 << '\n';
        reco1(where1, f[where1]);
        sort(sol.begin(), sol.end());
    }
    else {
        cout << ans2 << '\n';
        reco2(where2, f[where2]);
        sort(sol.begin(), sol.end());
    }
    cout << sol.size() << '\n';
    az=89;
    for (int i = 0; i < sol.size(); ++ i)
        cout << sol[i] << " \n"[i + 1 == sol.size()];
    return 0;
}

