// problem statement is in https://www.hackerrank.com/challenges/ctci-linked-list-cycle/problem
#include <bits/stdc++.h>

#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<cmath>
using namespace std;
struct Node
{
	int data;
	Node* next;
};/*
  Detect loop in a linked list 
  List could be empty also
  Node is defined as 
  struct Node
  {
     int data;
     struct Node *next;
  }
*/
int HasCycle(Node* head)
{
   if(head == NULL) return 0;

   Node *slow, *fast;
    slow = head;
    fast = head;

    while(fast && slow){
        fast = fast->next;

        if(fast == NULL){ //No loop
            return 0;
        }

        if(fast == slow){ //loop found
            return 1;
        }
        fast = fast->next;

        if(fast == slow){
            return 1;
        }
        if(fast == NULL)
            return 0;

        slow = slow->next;
    }

    return 1;
}

